import { ethers } from "ethers";
import detectEthereumProvider from '@metamask/detect-provider';

function App() {
    const [storedPrice, setStoredPrice] = useState('');
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    var signer ;
    const connectWallet = async () => {
        await provider.send('eth_requestAccounts', []);


        }
    signer = provider.getSigner();
    console.log(provider,"providerhere")
    connectWallet();
    const contractAddress = '0x033f2370ceefdcd8a1abd90b6de6b2da0c72e5a2';
    const ABI = '[ { "anonymous": false, "inputs": [ { "indexed": true, "internalType": "uint256", "name": "_memberId", "type": "uint256" } ], "name": "savingsEvent", "type": "event" }, { "inputs": [ { "internalType": "string", "name": "_name", "type": "string" }, { "internalType": "uint256", "name": "_balance", "type": "uint256" } ], "name": "addMember", "outputs": [], "stateMutability": "nonpayable", "type": "function" }, { "inputs": [], "stateMutability": "nonpayable", "type": "constructor" }, { "inputs": [ { "internalType": "uint256", "name": "_memberId", "type": "uint256" } ], "name": "get", "outputs": [ { "components": [ { "internalType": "uint256", "name": "id", "type": "uint256" }, { "internalType": "string", "name": "name", "type": "string" }, { "internalType": "uint256", "name": "balance", "type": "uint256" } ], "internalType": "struct SavingsAccount.Member", "name": "", "type": "tuple" } ], "stateMutability": "view", "type": "function" }, { "inputs": [], "name": "getMember", "outputs": [ { "internalType": "uint256[]", "name": "", "type": "uint256[]" }, { "internalType": "string[]", "name": "", "type": "string[]" }, { "internalType": "uint256[]", "name": "", "type": "uint256[]" } ], "stateMutability": "view", "type": "function" }, { "inputs": [], "name": "getMembers2", "outputs": [ { "components": [ { "internalType": "uint256", "name": "id", "type": "uint256" }, { "internalType": "string", "name": "name", "type": "string" }, { "internalType": "uint256", "name": "balance", "type": "uint256" } ], "internalType": "struct SavingsAccount.Member[]", "name": "", "type": "tuple[]" } ], "stateMutability": "view", "type": "function" }, { "inputs": [], "name": "memberCount", "outputs": [ { "internalType": "uint256", "name": "", "type": "uint256" } ], "stateMutability": "view", "type": "function" }, { "inputs": [ { "internalType": "uint256", "name": "", "type": "uint256" } ], "name": "members", "outputs": [ { "internalType": "uint256", "name": "id", "type": "uint256" }, { "internalType": "string", "name": "name", "type": "string" }, { "internalType": "uint256", "name": "balance", "type": "uint256" } ], "stateMutability": "view", "type": "function" } ]'
    const contract = new ethers.Contract(contractAddress, ABI, signer);

    const getStoredPrice = async () => {
      try {
        const contractPrice = await contract.getMember();
        console.log(contractPrice.toString())
      } catch (error) {
        console.log("getStoredPrice Error: ", error);
      }
    }

    async function updateNewPrice() {
      try {
        const transaction = await contract.addMember("abeer",6);
        await transaction.wait();
        await getStoredPrice();
      } catch (error) {
        console.log("updateNewPrice Error: ", error);
      }

    }

    getStoredPrice()
    .catch(console.error)
      return (
        <div className="container">
          <div className="row mt-5">

            {/* <div className="col">
              <h3>Stored Price</h3>
              <p>Stored ETH/USD Price: {storedPrice}</p>
            </div> */}

            <div className="col">
              <h3>Update Price</h3>
              <button type="submit" className="btn btn-dark" 
    onClick={updateNewPrice}>Update</button>
            </div>

            <div className="col">
              <h3>Stored Price</h3>
              <button type="submit" className="btn btn-dark" 
    onClick={storedPrice}>Update</button>
            </div>
            {/* {contractPrice && contractPrice.map(user =>
                        <tr key={user.id}>
                            <td>{user.firstName} {user.lastName}</td>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                        </tr>
                    )} */}
          </div>
        </div>

      );

}
export default App;
