import React, { useEffect, useState } from 'react';
import { ethers } from "ethers";
import detectEthereumProvider from '@metamask/detect-provider';

function App() {
    const [storedPrice, setStoredPrice] = useState('');
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    var signer ;
    const connectWallet = async () => {
        await provider.send('eth_requestAccounts', []);


        }
    signer = provider.getSigner();
    console.log(provider,"providerhere")
    connectWallet();
    const contractAddress = '0x7F9ac32dC8bDb57A6a3d44E67ab8ef08923492E1';
    const ABI = '[{"inputs":[{"internalType":"string","name":"product_name","type":"string" }, { "internalType": "uint256", "name": "quantity", "type": "uint256" } ], "name": "AddProducts", "outputs": [], "stateMutability": "nonpayable", "type": "function" }, { "inputs": [], "name": "getAllProducts", "outputs": [ { "components": [ { "internalType": "uint256", "name": "quantity", "type": "uint256" }, { "internalType": "string", "name": "product_name", "type": "string" } ], "internalType": "struct C.Product[]", "name": "", "type": "tuple[]" } ], "stateMutability": "view", "type": "function" } ]'
    const contract = new ethers.Contract(contractAddress, ABI, signer);

    const getStoredPrice = async () => {
        try {
          const contractPrice = await contract.getAllProducts();
          await transaction.wait();
          console.log(contractPrice);
        } catch (error) {
          console.log("getStoredPrice Error: ", error);
        }
      }

      async function updateNewPrice() {
        try {
          const transaction = await contract.AddProducts("bread",5);
          console.log("update called")
          await transaction.wait();
          await getStoredPrice();
        } catch (error) {
          console.log("updateNewPrice Error: ", error);
        }

      }

      getStoredPrice()
      .catch(console.error,"errorhere")

      return (
        <div className="container">
          <div className="row mt-5">

            <div className="col">
              <h3>Stored Price</h3>
              <p>Stored ETH/USD Price: {storedPrice}</p>
            </div>

            <div className="col">
              <h3>Update Price</h3>
              <button type="submit" className="btn btn-dark" 
    onClick={updateNewPrice}>Update</button>
            </div>
          </div>
        </div>
      );

}
export default App;
