}, { "internalType": "string", "name": "name", "type": "string" }, { "internalType": "uint256", "name": "balance", "type": "uint256" } ], "stateMutability": "view", "type": "function" } ]'
    const contract = new ethers.Contract(contractAddress, ABI, signer);


    const getStoredPrice = async () => {
      try {
        const contractPrice = await contract.getMembers2();
