      }
    }

    async function updateNewPrice() {
      try {
        const transaction = await contract.addMember("abeer",6);
        await transaction.wait();
        await getStoredPrice();
      } catch (error) {
        console.log("updateNewPrice Error: ", error);
      }

    }
