  const NamesList = () => (
    <div>
      <ul>{names.map(name => <li key={name}> {name} </li>)}</ul>
    </div>
  );
      return (
        <div className="container">
          <div className="row mt-5">

             <div className="col">
              <h3>Stored Price</h3>
              <p>Stored ETH/USD Price: {storedPrice}</p>
            </div> 

            <div className="col">
              <h3>Update Price</h3>
              <button type="submit" className="btn btn-dark" 
    onClick={updateNewPrice}>Update</button>
            </div>

            <div className="col">
              <h3>Stored Price</h3>
              <button type="submit" className="btn btn-dark" 
    onClick={getStoredPrice}>Update</button>
            </div>
    {  NamesList}
          </div>
        </div>

      );

}

export default App;
